        return new Source("{% extends 'base.html.twig' %}

{% block title %}Parties{% endblock %}

{% block body %}

    <h1 class=\"mt-3 mx-3\">Parties</h1>

    <a href=\"{{ path('app_backoffice_game_new') }}\" class=\"btn btn-outline-info mx-3 float-end\">Create new</a>
    <table class=\"table table-hover\">
        <thead>
            <tr>
                <th scope=\"col\">Id</th>
                <th scope=\"col\">Name</th>
                <th scope=\"col\">Status</th>
                <th scope=\"col\">DM</th>
                <th scope=\"col\">Actions</th>
            </tr>
        </thead>
        <tbody>
        {% for game in games %}
            <tr class=\"table-active\">
                <td>{{ game.id }}</td>
                <td>{{ game.name }}</td>
                <td>{{ game.status }}</td>
                <td>{{ game.dm.login }}</td>
                <td>
                    <a href=\"{{ path('app_backoffice_game_show', {'id': game.id}) }}\"><i class=\"bi bi-eye btn btn-sm btn-success rounded\"></i></a>
                    <a href=\"{{ path('app_backoffice_game_edit', {'id': game.id}) }}\"><i class=\"bi bi-pencil btn btn-sm btn-warning rounded\"></i></a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

{% endblock %}", "backoffice/game/index.html.twig", "/var/www/html/Protons/ODicey/projet-04-o-dicey-back/templates/backoffice/game/index.html.twig");
